(1534, 510, '_form', '<div class=\"form-wrapper\">\n\n<div class=\"row\">\n<div class=\"col-md-12\">\n<div class=\"title\">\n<h4>Request an estimate</h4>\n<h3>OUR SERVICES</h3>\n</div>\n</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-6\">First name [text* first-name placeholder \"Enter your first name\"]</div>\n<div class=\"col-md-6\">Last name [text* last-name placeholder \"Enter your last name\"]</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-6\">Your address [text* your-address placeholder \"Your address\"]</div>\n<div class=\"col-md-6\">Your phone [text* your-phone placeholder \"Your phone\"]</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-6\">Your email [email* your-email placeholder \"Your email\"]</div>\n<div class=\"col-md-6\">Motorcycle type [select motorcycle-type \"Standard\" \"Cruiser\" \"Touring\" \"Sport\" \"Scooters\"]</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-6\">Services [select services \"Motorcycle repair\" \"Engine rebuilds\" \"Custom fabrication services\" \"Transmission work\" \"Big Bore Kits\"]</div>\n<div class=\"col-md-6\">Your buget [select your-budget \"1000$\" \"2000$\" \"5000$\" \"10000$\"]</div>\n</div>\n</div>\n\n<div class=\"form-button button-effect1 theme-bg\">[submit \"SUBMIT\"]<span class=\"icon-effect\"></span> <span class=\"note\">Your information will be safe</span></div>');
