(1542, 605, '_form', '<div class=\"booking-services\">\n\n<div class=\"form-wrapper\">\n\n<div class=\"row\">\n<div class=\"col-md-12\">\n<div class=\"form-title\">\n<h3>Personal and Contact Info</h3>\n</div>\n</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-4\">FIRST NAME [text* first-name placeholder \"Enter your first name\"]</div>\n<div class=\"col-md-4\">LAST NAME [text* last-name placeholder \"Enter your last name\"]</div>\n<div class=\"col-md-4\">YOUR PHONE [text* your-phone placeholder \"Your phone\"]</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-4\">YOUR EMAIL [email* your-email placeholder \"Your email\"]</div>\n<div class=\"col-md-8\">YOUR ADDRESS [text* your-address placeholder \"Your address\"]</div>\n</div>\n\n</div>\n\n<div class=\"form-wrapper\">\n\n<div class=\"row\">\n<div class=\"col-md-12\">\n<div class=\"form-title\">\n<h3>your motorcycle info</h3>\n</div>\n</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-4\"><div class=\"motorcycle\">MOTORCYCLE BRAND [select motorcycle-type \"Honda\" \"Yamaha\" \"BMW\" \"Harley-Davidson\" \"Ducati\"]</div></div>\n<div class=\"col-md-4\"><div class=\"motorcycle\">MOTORCYCLE TYPE [select  motorcycle-type \"Standard\" \"Cruiser\" \"Touring\" \"Sport\" \"Scooters\"]</div></div>\n<div class=\"col-md-4\">MOTORCYCLE NAME [text* motorcycle-name placeholder \"Motorcycle name\"]</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-4\"><div class=\"motorcycle\">MOTORCYCLE REPAIR SERVICES [select services multiple \"Motorcycle repair\" \"Engine rebuilds\" \"Custom fabrication services\" \"Transmission work\" \"Big Bore Kits\"]</div></div>\n<div class=\"col-md-4\">BOOKING DATE [text* booking-date placeholder \"dd/mm/yyy\"]</div>\n<div class=\"col-md-4\">FINISH AS EXPECTED DATE [text* finish-date-as-expected placeholder \"dd/mm/yyy\"]</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-12 textarea\">YOUR MESSAGE [textarea your-message placeholder \"Your message\"]</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-12 check-box\">[checkbox* use_label_element \"\"]I have read and agree to our <span class=\"theme-color\">Terms and Conditions</span> and our <span class=\"theme-color\">Privacy Policy</span>. </div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-12 form-button-submit\"><span class=\"button-effect theme-bg\"><i class=\"fa fa-check-square\"></i>[submit class:theme-bg \"BOOKING SERVICES\"]<span class=\"text-hover\">BOOKING SERVICES</span><span></div>\n</div>\n\n</div>\n\n</div>'),
